import React, { Component } from 'react'
import { withStyles } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'
import { getPostsByUserId, getUserProfile, followUser, unfollowUser, refreshUserProfile } from '../../actions/profileActions'
import Post from '../Posts/Post'
import LoadingPosts from '../Posts/LoadingPosts'
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button'

const styles = {
  handle: {
    color: '#8899a6',
    paddingLeft: 5,
    display: 'inline-block',
    '&:hover': {
      textDecoration: 'none'
    },
    fontSize: 14,
    fontWeight: 400
  },
  display: {
   marginTop: 5,
   marginBottom: 5
  },
  paper: {
    padding: '1rem',
    borderRadius: 0,
  },
  detailBlock: {
    display: 'flex',
    marginTop: 10
  },
  detail: {
    marginRight: '1.5rem',
    color: '#8899a6',
    display: 'block',
    textAlign: 'left',
    fontWeight: 700,
    fontSize: 12
  },
  profile: {
    borderRadius: 0,
    backgroundColor: '#15202b',
    padding: '1rem',
    border: '1px solid #8899a6' ,
  },
  link: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 700
  },
  value: {
    color: '#19cf86',
    fontWeight: 700,
    fontSize: 18,
    textAlign: 'left'
  }
}

class Profile extends Component {
  constructor(props) {
    super(props)

    this.handleFollow = this.handleFollow.bind(this)
    this.handleUnfollow = this.handleUnfollow.bind(this)
  }

  handleFollow () {
    this.props.followUser(this.props.profile._id)
  }

  handleUnfollow () {
    this.props.unfollowUser(this.props.profile._id)
  }

  render() {
    const classes = this.props.classes
    const profile = this.props.profile
    let followBtn
    if(this.props.auth.isAuthenticated) {
      if(this.props.user.following.indexOf(this.props.profile._id) === -1) {
        followBtn = (
          
            <Button variant="outlined" type="" className = { classes.btnFollow } onClick = { this.handleFollow }>
              Follow
            </Button>

        )
      } else {
        followBtn = (
          <span className = { classes.btnBlock }>
            <Button variant="outlined" type="" className = { classes.btnFollow } onClick = { this.handleUnfollow }>
              Unfollow
            </Button>
          </span>
        )
      }
    } 


    let profileInfo
    if(profile) {
      profileInfo = (
        <Paper className = { classes.profile }>
          <Link href = { `/profile/${profile._id}` } className = { classes.link }> 
            { profile.login }
            <span className = { classes.handle }>
              @{ profile.handle }
            </span>
          </Link>
          <div className = { classes.detailBlock }>
            <div className = { classes.detail }>
              <span className = { classes.followers }>
                Followers
              </span>
              <div className = { classes.value }>
                { profile.followers.length }
              </div>
            </div>


            <div className = { classes.detail }>
              <span className = { classes.following }>
                Following
              </span>
              <div className = { classes.value }>
                { profile.following.length }
              </div>
            </div>
          </div>
        </Paper>
        
      )
    }
    return (
      <div>{ profileInfo }</div>
    )
  }
}

const mapStateToProps = (state) => ({
  user: state.auth.user,
  auth: state.auth
})

export default connect(mapStateToProps, {  followUser, unfollowUser, refreshUserProfile })(withRouter(withStyles(styles)(Profile)))
