import {
  SET_CURRENT_USER,
  FOLLOW,
  UNFOLLOW
} from '../constants'

const initialState = {
  isAuthenticated: false,
  user: null
}

export default function( state = initialState, action ) {
  if(action.payload == null) { action.payload = '' }
  switch(action.type) {
    case SET_CURRENT_USER:
      return {
        ...state,
        isAuthenticated: (Object.keys(action.payload).length != 0 ? true : false),
        user: action.payload 
      }
    case FOLLOW:
      return {
        ...state,
        user: {
          ...state.user,
          following: [...state.user.following, action.payload]
        }
      }
    case UNFOLLOW:
      return {
        ...state,
        user: {
          ...state.user,
          following: state.user.following.filter(item => item != action.payload)
        }
      }
    default:
      return state
  }
}
