import React, { Component } from 'react'
import { withStyles } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import { getPostsByUserId, getUserProfile } from '../../actions/profileActions'
import Post from '../Posts/Post'
import LoadingPosts from '../Posts/LoadingPosts'
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button'

const styles = {
  handle: {
    color: '#888',
    display: 'inline-block',
    '&:hover': {
      textDecoration: 'none'
    }
  },
  following: {

  },
  followers: {

  },
  display: {
   marginTop: 5,
   marginBottom: 5
  },
  paper: {
    padding: 8
  },
  detailBlock: {
    display: 'flex'
  },
  detail: {
    marginRight: 5
  },
  follow: {
    float: 'right',
    paddingBottom: 3
  }
}

class Profile extends Component {
  constructor(props) {
    super(props)
  }
  componentDidMount() {
    this.props.getPostsByUserId(this.props.match.params.userId)
    this.props.getUserProfile(this.props.match.params.userId)
  }

  render() {
    const { classes, loadingPosts, loadingProfile, list, auth, user, profile } = this.props
    console.log(profile)
    let items;
    items = list && list.map(el => <Post key={ el._id } post={ el }/>)
    let profileInfo;
    if(profile && items) {
      profileInfo = (
        <Paper className = { classes.paper }>
          <h1 className = { classes.display }>
            { profile.login }
            <span className = { classes.follow }>
            <Button variant="outlined" type="">
                Follow
              </Button>
            </span>
          </h1>
          <div className = { classes.handle }>@{ profile.handle }</div>
          <div className = { classes.detailBlock }>
            <div  className = { classes.detail }>
              { items.length }
              <span> posts</span>
            </div>
            <div  className = { classes.detail }>
              <span className = { classes.followers }>
                { profile.followers.length + ' ' }
                followers
              </span>
            </div>
            <div  className = { classes.detail }>
              <span className = { classes.following }>
                { profile.following.length + ' ' }
                following
              </span>
            </div>
          </div>
        </Paper>

      )
    }

    return (
      <div>
        { loadingProfile ? <div>Loading...</div> : profileInfo }
        { loadingPosts ? <LoadingPosts/> : items}
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  loadingPosts: state.post.loading,
  list: state.post.list,
  profile: state.profile.user,
  loadingProfile: state.profile.loading,
  auth: state.auth,
  user: state.auth.user
})

export default connect(mapStateToProps, { getPostsByUserId, getUserProfile })(withStyles(styles)(Profile))
