import {
  LOAD_PROFILE,
  GET_PROFILE,
} from '../constants'

const intialState = {
  loading: false,
  user: null
}

export default function ( state = intialState, action) {
  switch(action.type) {
    case LOAD_PROFILE:
      return {
        ...state,
        loading: true
      }
      case GET_PROFILE:
        return {
          ...state,
          loading: false,
          user: action.payload
        }
    default:
      return state
  }
}
