import React, { Component } from 'react'
import SearchProfile from './SearchProfile'
import { connect } from 'react-redux'
import { searchUser } from '../../actions/searchActions'

class ListProfile extends Component {
  componentDidMount() {
    let query = { "text": this.props.query }
    this.props.searchUser(query)
  }
  render() {
    const { list, loading } = this.props
    let items
    if(list) {
      items = list && list.map(el => <SearchProfile key={el._id} profile={el}/>)
      console.log(items[0])
    }
    return (
      <div>
        { loading ? <div></div> : items }
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  list: state.profile.list,
  loading: state.profile.loading
})

export default connect(mapStateToProps, { searchUser })(ListProfile)
