import React, { Component } from 'react'
import Paper from '@material-ui/core/Paper'
import { connect } from 'react-redux'
import { getPostsByUserId, getUserProfile } from '../../actions/profileActions'

class Profile extends Component {
  componentDidMount() {
    this.props.getPostsByUserId(this.props.user.id)
    this.props.getUserProfile(this.props.user.id)
  }

  render() {
    const { user, auth, posts } = this.props
    console.log(this.props)
    return (
      <Paper>
        <h1>
          { user.login }
        </h1>
        <h2>
          { user.handle }
        </h2>

      </Paper>
    )
  }
}

const mapStateToProps = (state) => (
  console.log(state),{
  auth: state.auth,
  user: state.auth.user,
  posts: state.profile.list,
  profile: state.profile.id
})

export default connect(mapStateToProps, { getPostsByUserId, getUserProfile })(Profile)
