import React, { Component } from 'react';
import AppBar from '@material-ui/core/AppBar';
import ToolBar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'
import AccountCircle from '@material-ui/icons/AccountCircle'

import { Link } from 'react-router-dom'
import { withStyles } from '@material-ui/core/styles'
import { fontSize } from '@material-ui/system';
import { connect } from 'react-redux'

import Icon from '../../icons/Icons'

const style = {
  root: {
    flexGrow: 1
  },
  logo: {
    color: '#fff',
    fontSize: 30,
    textTransform: 'uppercase'
  }
}

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      anchorEl: null
    }
  }

  handleMenu = (event) => { this.setState({ anchorEl: event.target.currentTarget }) }
  handleColor = () => { this.setState({ anchorEl: null })}
  render() {
    const { classes } = this.props
    const { anchorEl } = this.state
    const open = Boolean(anchorEl)
    const authLinks = (
      <div>
        <IconButton
          aria-owns = { open ? 'menu' : undefined }
          aria-haspopup = "true"
          color = "inherit"
          onClick = { this.handleMenu }
        >
          <AccountCircle/>
        </IconButton>
        <Menu
          id="menu"
          anchorEl = { anchorEl }
        >
          <MenuItem>
          </MenuItem>
        </Menu>
      </div>
    )
    return (
      <div classname = {classes.root}>
        <AppBar position="static" style={{ backgroundColor: '#78dd60' }}>
          <ToolBar>
            <Link to="/" className={ classes.logo }><Icon name ="home" width={70} fill={'#fff'}/></Link>
          </ToolBar>
        </AppBar>
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth
})

export default connect(mapStateToProps)(withStyles(style)(Header))
