import {
  SET_CURRENT_USER
} from '../constants'
import { CardActions } from '@material-ui/core';

const initialState = {
  isAuthenticated: false,
  user: null
}

export default function( state = initialState, action ) {
  switch(action.type) {
    case SET_CURRENT_USER:
      return {
        ...state,
        isAuthenticated: Object.keys(action.payload).length !== 0,
        user: action.payload 
      }
    default:
      return state
  }
}
