import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles';
import { withRouter } from 'react-router-dom'
import { connect } from 'react-redux'
import { registerUser } from '../../actions/authActions'

const styles = {
  TextField: {
    width: '100%',
    marginBottom: 5
  },
  btnBlock: {
    textAlign: 'center'
  }
}

class Register extends Component {
  constructor(props) {
    super(props)
    this.state = {
      email: '',
      login: '',
      handle: '',
      password: '',
      password2: '',
      errors: {}
    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)  
  }
  componentWillReceiveProps(nextProps) {
    if(nextProps.errors) {
      this.setState({ errors: nextProps.errors })
    } 
  }
  handleChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  handleSubmit(e) {
    e.preventDefault()
    const userData =  {
      email: this.state.email,
      login: this.state.login,
      handle: this.state.handle,
      password: this.state.password,
      password2: this.state.password2
    }
    this.props.registerUser(userData, this.props.history)
  }
  render() {
    const { classes } = this.props;
    const { errors } = this.state
    return (
      <Paper style = {{ padding: 15 }} >
        <form onSubmit={this.handleSubmit}>
          <TextField 
            type = "email"
            label = "Email"
            className={classes.TextField}
            value = {this.state.email}
            onChange = {this.handleChange}
            name = "email"
            helperText = { errors.email ? errors.email : '' }
            error = { errors.email ? true : false }
          />
          <TextField 
            label = "Display Name"
            type = "text"
            className={classes.TextField}
            value = {this.state.login}
            onChange = {this.handleChange}
            name = "login"
            helperText = { errors.login ? errors.login : '' }
            error = { errors.login ? true : false }
          />
          <TextField 
            label = "Handle"
            type = "text"
            className={classes.TextField}
            value = {this.state.handle}
            onChange = {this.handleChange}
            name = "handle"
            helperText = { errors.handle ? errors.handle : '' }
            error = { errors.handle ? true : false }
          />
          <TextField 
            label = "Password"
            type = "password"
            className={classes.TextField}
            value = {this.state.password}
            onChange = {this.handleChange}
            name = "password"
            helperText = { errors.password ? errors.password : '' }
            error = { errors.password ? true : false }
          />
          <TextField 
            label = "Confirm Password"
            type = "password"
            className={classes.TextField}
            value = {this.state.password2}
            onChange = {this.handleChange}
            name = "password2"
            helperText = { errors.password2 ? errors.password2 : '' }
            error = { errors.password2 ? true : false }
          />
          <div class={classes.btnBlock}>
            <Button variant="outlined" type="">
              Submit
            </Button>
          </div> 
        </form> 
      </Paper>
    )
  }
}

const mapStateToProps = (state) => ({
  errors: state.errors
})

export default connect(mapStateToProps, { registerUser })(withRouter(withStyles(styles)(Register)))
