import React, { Component } from 'react'
import { withStyles } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import { getPostsByUserId, getUserProfile, followUser, unfollowUser, refreshUserProfile } from '../../actions/profileActions'
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button'

const styles = {
  handle: {
    color: '#888',
    display: 'inline-block',
    '&:hover': {
      textDecoration: 'none'
    }
  },
  following: {

  },
  followers: {

  },
  display: {
   marginTop: 5,
   marginBottom: 5
  },
  paper: {
    padding: 8
  },
  detailBlock: {
    display: 'flex'
  },
  detail: {
    marginRight: 5
  },
  btnBlock: {
    float: 'right',
  },
  btnFollow: {
    backgroundColor: 'inherit',
    color: '#78dd60',
    '&:hover': {
      color: '#fff',
      borderColor: '#78dd60',
      backgroundColor: '#78dd60'
    }
  }
}

class Profile extends Component {
  constructor(props) {
    super(props)

    this.handleFollow = this.handleFollow.bind(this)
    this.handleUnfollow = this.handleUnfollow.bind(this)
  }

  componentDidMount() {
    getUserProfile(this.props.targetUser)
    getPostsByUserId(this.props.targetUser)
  }

  componentWillMount() {
    getUserProfile(this.props.targetUser)
    getPostsByUserId(this.props.targetUser)
  }

  componentDidUpdate(oldProps) {
    if(this.props.auth.isAuthenticated) {
      console.log(this.props)
      if(oldProps.user && oldProps.user.following !== this.props.user.following) {
        this.props.user.following.map((id, i) => {
          this.props.getUserProfile(id)
        })
      }
      console.log(this.props)
    } 
  }

  handleFollow () {
    this.props.followUser(this.props.profile._id)
  }

  handleUnfollow () {
    this.props.unfollowUser(this.props.profile._id)
  }


  render() {
    const { classes, loadingProfile, list, auth, user, profile } = this.props
    console.log(this.props)
    let followBtn
    
     
    let items;
    items = list 
    let profileInfo;
    if(profile && items) {
      profileInfo = (
        <Paper className = { classes.paper }>
          <h1 className = { classes.display }>
            { profile.login }
            { followBtn }
          </h1>
          <div className = { classes.handle }>@{ profile.handle }</div>
          <div className = { classes.detailBlock }>
            <div  className = { classes.detail }>
              { items.length }
              <span> posts</span>
            </div>
            <div  className = { classes.detail }>
              <span className = { classes.followers }>
                { profile.followers.length + ' ' }
                followers
              </span>
            </div>
            <div  className = { classes.detail }>
              <span className = { classes.following }>
                { profile.following.length + ' ' }
                following
              </span>
            </div>
          </div>
        </Paper>

      )
    }

    return (
      <div>
        { loadingProfile ? <div>Loading...</div> : profileInfo }
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  list: state.post.list,
  profile: '',
  loadingProfile: state.profile.loading,
  auth: state.auth,
  user: state.auth.user
})

export default connect(mapStateToProps, { getPostsByUserId, getUserProfile, followUser, unfollowUser, refreshUserProfile })(withStyles(styles)(Profile))
